                                var g = this.image.getContext("2d");
                                g.fillStyle = e.shiftKey ? this.bg.toHexString() : this.fg.toHexString();
                                plotLine(g,Math.floor(ipos[0]),Math.floor(ipos[1]),Math.floor(i2pos[0]),Math.floor(i2pos[1]));
