define('site/LoginPanel.js',["jquery","text!site/LoginPanel.html","bootstrap"],
function($,template) {
    var This = function() {
        this.init.apply(this,arguments);
    }

    $.extend(This.prototype, {
        init:function(main) {
            this.main = main;
            this.$el = $("<div class='loginPanel'/>");
            this.$el.html(template);
            this.currentUser = false;
            this.updateDisplay();

            $.ajaxPrefilter( function( options, originalOptions, jqXHR ) {
              options.crossDomain ={
                crossDomain: true
              };
              options.xhrFields = {
                withCredentials: true
              };
            });

            if (this.main.editorActive) {
                this.$el.find(".editorLink").addClass("active");
            } else {
                this.$el.find(".browseLink").addClass("active");
            }

            this.refreshCurrentUser();

            var $loginModal = this.$el.find(".loginModal");
            var $registerModal = this.$el.find(".registerModal");

            this.$el.find(".loginLink").click(_.bind(function() {
                $loginModal.modal("show");
                return false;
            },this));

            /*
            this.$el.find(".registerLink").click(_.bind(function() {
                $registerModal.modal("show");
                return false;
            },this));
            */

            this.$el.find(".logoutLink").click(_.bind(function() {
                this.logout();
                return false;
            },this));

            $loginModal.find("form").submit(_.bind(function() {
                var email = $loginModal.find(".email").val();
                var password = $loginModal.find(".password").val();

                $loginModal.modal("hide");

                this.login(email,password);
                return false;
            },this));
        },
        refreshCurrentUser:function() {
            console.log("refreshing current user");
            var opt = {
                type:"GET",
                url:this.main.host+"/user/current",
                dataType:"json"
            };
            $.ajax(opt).done(_.bind(function(res) {
                this.currentUser = res;
                $(this).trigger("UserUpdated",[this.currentUser]);
                console.log("got user",this.currentUser);

                this.updateDisplay()
            },this));
        },
        login:function(email,password) {
            var opt = {
                type:"POST",
                dataType:"json",
                url:this.main.host+"/user/login",
                headers: { "Authorization": "Basic " + btoa(email + ":" + password) },
            };

            $.ajax(opt).success(_.bind(function(res) {
                this.currentUser = res;
                $(this).trigger("UserUpdated",[this.currentUser]);

                this.updateDisplay()
            },this));
        },
        logout:function() {
            var opt = {
                type:"POST",
                url:this.main.host+"/user/logout",
            };

            $.ajax(opt).success(_.bind(function(res) {
                this.currentUser = false;
                $(this).trigger("UserUpdated",[this.currentUser]);

                this.updateDisplay()
            },this));
        },
        updateDisplay:function() {
            if (!this.currentUser) {
                $(document.body).removeClass("loggedIn");
                return;
            }
            $(document.body).addClass("loggedIn");
            this.$el.find(".loginName").text(this.currentUser.display);
        },
    });

    return This;
});

define('view/SelectList.js',['jquery','view/util.js'],function($,util) {
    var This = function(data,renderer) {
        this.init.apply(this,arguments);
    }

    jQuery.fn.insertAt = function(index, element) {
        var lastIndex = this.children().size()
        if (index < 0) {
            index = Math.max(0, lastIndex + 1 + index)
        }
        this.append(element)
        if (index < lastIndex) {
            this.children().eq(index).before(this.children().last())
        }
        return this;
    }

    $.extend(This.prototype, {
        defaultOpts:{
            multiple:true,
            moreRenderer:function() {
                return $("<a href='#'>more</a>");
            }
        },
        init:function(data,renderer,rendererthis,opts,grouprenderer) {
            this.rendererthis = rendererthis;
            this.$el = $("<ul class='list-group scrollable'/>");
            this.$el.attr("tabindex","0");
            this.$el.css("user-select","none");
            this.selectedIndexes = [];
            this.cursorIndex = -1;
            this.opts = opts ? opts : this.defaultOpts;

            this.renderer = renderer;
            this.grouprenderer = grouprenderer;

            this.addElements(data);

            this.$el.focus(_.bind(this.focused,this));
            this.$el.blur(_.bind(this.blurred,this));
            this.$el.keydown(_.bind(this.keyDown,this));
        },
        processPagination(data) {
            if (data && data.results) {
                this.pagination = data;
                data = data.results;
                delete(this.pagination.results);
            } else {
                this.pagination = null;
            }

            return data;
        },
        addElements:function(data) {
            data = this.processPagination(data);

            var self = this;
            function addGroup(items,group) {
                _.each(items,_.bind(function(value,index) {
                    value.index = index;
                    var $el = this.renderer.call(this.rendererthis,value);
                    $el.data("index",index);
                    $el.data("object",value);
                    $el.data("group",group);
                    this.addBehavior($el);
                    this.$el.append($el);
                },self));
            }

            if ($.isArray(data)) {
                addGroup(data,null);
            } else {
                _.each(data,function(items,group) {
                    addGroup(items,group);
                });
            }
            this.refreshGroupings();
        },
        addElement:function(element,group,index) {
            var index = index === undefined ?  this.$el.find(".listElement").length-1 : index;
            var $el = this.renderer.call(this.rendererthis,element);

            this.$el.insertAt(index,$el);
            this.addBehavior($el);
            $el.data("index",index);
            $el.data("object",element);
            $el.data("group",group);

            this.refreshGroupings();
        },
        refreshGroupings:function() {
            this.$el.find(".moreLink").remove();

            var groupMap = {};
            groupMap[""] = [];
            this.$el.find(".listElement").each(function() {
                var group = $(this).data("group");
                group = group || "";
                if (!groupMap[group]) groupMap[group] = [];
                groupMap[group].push(this);
            });

            var keys = Object.keys(groupMap);
            keys.sort(function(a,b) {
                if (a == '' && b == '') return 0;
                if (a == '') return 1;
                if (b == '') return -1;
                return a.localeCompare(b);
            });

            this.$el.children().detach();
            var index = 0;
            _.each(keys,_.bind(function(group) {
                var header = group == "" ? "Ungrouped" : group;
                if (this.grouprenderer && keys.length > 1 && groupMap[group].length > 0) {
                    var $groupHeader = this.grouprenderer(header);
                    util.bindClickEvent($groupHeader,_.bind(function() {
                        //group clicked
                        this.$el.find(".selected").removeClass("selected");
                        $groupHeader.addClass("selected");
                        var args = [false,false,header];
                        $(this).trigger("change",args);
                    },this));
                    this.$el.append($groupHeader);
                }
                _.each(groupMap[group],_.bind(function(item) {
                    $(item).data("index",index++);
                    this.$el.append(item);
                },this));
            },this));


            if (this.pagination && this.pagination.page < this.pagination.totalPages-1) {
                var $more = this.opts.moreRenderer();
                $more.addClass("moreLink");
                $more.click(_.bind(function() {
                    $(this).trigger("MoreClicked",[this.pagination]);
                },this));
                this.$el.append($more);
            }
        },
        updateElement:function(element) {
            var self = this;
            this.$el.find(".listElement").each(function() {
                if ($(this).data("object") == element) {
                    self.renderer.call(self.rendererthis,element,$(this));
                }
            });
        },
        refresh:function() {
            var self = this;
            this.$el.children(".listElement").each(function() {
                self.renderer.call(self.rendererthis,$(this).data("object"),$(this));
            });
        },
        each:function(cb) {
            this.$el.children(".listElement").each(function() {
                var obj = $(this).data("object");
                var index = $(this).data("index");
                var $el = $(this);
                cb(obj,$el);
            });
        },
        focused:function(e) {
            this.focused = true;
        },
        blurred:function(e) {
            this.focused = false;
        },
        keyDown:function(e) { //todo: handle ctrl-a, ctrl-c
            if (this.focused) {
                if (e.keyCode == 40) { //down
                    e.preventDefault();
                    if (this.selectedIndexes.length == 0) {
                        this.select(0);
                    } else if (this.selectedIndexes.length >= 1) {
                        this.select(this.cursorIndex+1,e.shiftKey);
                    }
                    this.scrollToIndex(this.cursorIndex);
                    return true;
                } else if (e.keyCode == 38) { //up
                    e.preventDefault();
                    if (this.selectedIndexes.length == 0) {
                        this.select(this.getSize()-1);
                    } else if (this.selectedIndexes.length >= 1) {
                        this.select(this.cursorIndex-1,e.shiftKey);
                    }
                    this.scrollToIndex(this.cursorIndex);
                    return true;
                }
            }
        },
        scrollToIndex:function(index) {
            var $el = this.$el.children(".listElement").eq(index);
            var currentScroll = this.$el.scrollTop();
            var elOffset = $el.offset().top - this.$el.offset().top + currentScroll;
            var elHeight = $el.outerHeight(true);
            var visibleHeight = this.$el.height();

            var visibleMin = currentScroll;
            var visibleMax = currentScroll+visibleHeight;

            var scrollingTo = null;
            if (elOffset+elHeight > visibleMax) {
                scrollingTo = currentScroll + (elOffset - visibleMax) + elHeight;
            } else if (elOffset < visibleMin) {
                scrollingTo = currentScroll + (elOffset - visibleMin);
            }

            if (scrollingTo != null) {
                this.$el.scrollTop(scrollingTo);
            }
        },
        getSize:function() {
            return this.$el.find(".listElement").length;
        },
        scrollToSelected:function() {
            if (this.selectedIndexes.length) {
                this.scrollToIndex(this.selectedIndexes[0]);
            }
        },
        select:function(index,appendSelection) {
            var selectedMap = {};
            if (index === null || index === undefined) {
            } else {
                if (index < 0) index = 0;
                if (index >= this.getSize()) index = this.getSize()-1;
                this.cursorIndex = index;

                if (appendSelection) {
                    if (!_.contains(this.selectedIndexes,index)) {
                        this.selectedIndexes.push(index);
                    }
                } else {
                    this.selectedIndexes = [index];
                }
                selectedMap = _.reduce(this.selectedIndexes,function(memo,ind) {
                    memo[ind] = true;
                    return memo;
                },{});
            }

            var i = 0;
            var self = this;
            this.$el.find(".selected").removeClass("selected");
            this.$el.children(".listElement").each(function() {
                $(this).toggleClass("selected",selectedMap[i] == true);
                i++;
            });

            var args = [this.getSelected(),this.selectedIndexes];
            $(self).trigger("change",args);
        },
        deselect:function() {
            this.select(null);
        },
        getSelected:function() {
            var selectedObjects = [];
            this.$el.children().each(function() {
                if ($(this).is(".selected")) {
                    selectedObjects.push($(this).data("object"));
                }
            });
            return selectedObjects;
        },
        addBehavior:function($el) {
            var $nodes = null;
            if ($el) {
                $nodes = $el;
            } else {
                $nodes = this.$el.children(".listElement");
            }
            var self = this;
            $nodes.each(function() {
                util.bindClickEvent($(this),_.bind(function(e) {
                    var clickedIndex = $(this).data("index");
                    if ($(this).is(".disabled")) return;
                    if (self.opts.multiple && e.shiftKey) {
                        var clickedIndex = $(this).data("index");
                        var min = Math.min(clickedIndex,self.cursorIndex);
                        var max = Math.max(clickedIndex,self.cursorIndex);
                        for (var i=min; i<=max; i++) {
                            self.select(i,true);
                        }
                    } else {
                        self.select($(this).data("index"),e.ctrlKey || e.altKey);
                    }
                },this));
            });
        }
    });

    return This;
});


